import React, { Component } from "react";
import Overview from "./components/Overview";
import uniqid from "uniqid";

class App extends Component {

  constructor() {
    super();

    this.state = {
      isInitiated: false,
      currentScore: 0,
      bestScore: 0,
      selected: [],
    };
  }

  select1 = (e) => {
    console.log(e.target.textContent);
    console.log(this.state.selected);
    let bol;

    if(this.state.selected.length===0){
      alert("none");
    }
    for(let i=0;i<this.state.selected.length;i++){
      if(e.target.textContent===this.state.selected[i]){
        this.setState({
          currentScore: 0,
        });
        bol=false;
      }else{
        bol=true;
      }
    }
    console.log(bol);

    this.setState({
      selected: this.state.selected.concat(e.target.textContent),
    });
  };

  componentDidMount(){
    console.log("Mounted");

  }
  componentDidUpdate(){

  }


  render() {
    const { currentScore, bestScore } = this.state;

    return (
      <div>
      <header>Memory Game</header>
      <div>Select an image, but never select it again.</div>
      <p id="para">Your current Score: {currentScore}</p>
      <p>Your Best Score: {bestScore}</p>
      <div className="grid">
        <div id="element" onClick={this.select1}>
        <div></div>
        Ayano
        </div>
        <div id="element" onClick={this.select1}>2</div>
        <div id="element">3</div>
        <div id="element">4</div>
      </div>


      <button onClick={this.handleChange}>add</button>

      </div>
    );
  }
}

export default App;
