import React, { Component } from "react";
import Overview from "./components/Overview";
import uniqid from "uniqid";

class App extends Component {
  constructor() {
    super();

    this.state = {
      isInitiated: false,
      currentScore: 0,
      bestScore: 0,
      images: {
        image1: false,
        image2: false,
        image3: false,
        image4: false,
        image5: false,
        image6: false,
        image7: false,
        image8: false,
        image9: false,
        image10: false,
        image11: false,
        image12: false
      },
    };
  }
  handleChange = (e) => {
    this.setState({
      currentScore: this.state.currentScore+1,
    });
   // if(this.state.bestScore<this.state.currentScore){
    //  this.setState({
      //  bestScore: this.state.currentScore,
     // });
   // }
  };

  componentDidMount(){
      this.setState({
        isInitiated: true,
      });
      const element1=document.getElementById("element1");
      const element2=document.getElementById("element2");
      const element3=document.getElementById("element3");
      const element4=document.getElementById("element4");
  }
  componentDidUpdate(){
    console.log("Updated"); 

      const elementsArray = document.querySelectorAll("#element");
      for (let i = 0; i < elementsArray.length; i++) {
        elementsArray[i].addEventListener("click", function() {
          alert(elementsArray[i].value) 
        });
      }

  }


  render() {
    const { currentScore, bestScore } = this.state;

    return (
      <div>
      <header>Memory Game</header>
      <div>Select an image, but never select it again.</div>
      <p id="para">Your current Score: {currentScore}</p>
      <p>Your Best Score: {bestScore}</p>

      <div className="grid">
        <div id="element">1</div>
        <div id="element">2</div>
        <div id="element">3</div>
        <div id="element">4</div>
      </div>

      <button onClick={this.handleChange}>add</button>
        <Overview/>
      </div>
    );
  }
}

export default App;
