import React, { Component } from "react";
import Overview from "./components/Overview";
import uniqid from "uniqid";

class App extends Component {
  constructor() {
    super();

    this.state = {
      isInitiated: false,
      currentScore: 0,
      bestScore: 0,
      selected: ["xd"],
    };
  }
  handleChange = (e) => {
    const { currentScore, bestScore, selected } = this.state;
    const elementsArray = document.querySelectorAll("#element");
    for (let i = 0; i < elementsArray.length; i++) {
      elementsArray[i].addEventListener("click", function() {
        alert("Text content is "+elementsArray[i].textContent+" and array id is "+i);



      });
    }
    this.setState({
      selected: this.state.selected.concat(elementsArray[i].textContent),
    }); 
    console.log(this.state.selected)
  };

  componentDidMount(){
    console.log("Mounted");
    this.setState({
      isInitiated: true,
    });
  }
  componentDidUpdate(){
    console.log("Updated"); 


  }


  render() {
    const { currentScore, bestScore } = this.state;

    return (
      <div>
      <header>Memory Game</header>
      <div>Select an image, but never select it again.</div>
      <p id="para">Your current Score: {currentScore}</p>
      <p>Your Best Score: {bestScore}</p>
      <div className="grid">
        <div id="element" onClick={this.handleChange}>
        <div></div>
        1
        </div>
        <div id="element">2</div>
        <div id="element">3</div>
        <div id="element">4</div>
      </div>


      <button onClick={this.handleChange}>add</button>

      </div>
    );
  }
}

export default App;
